mapper.select:
SELECT board_id, parent_id, user_id, title, writer, detail, views, insert_date, update_date FROM BOARD WHERE use_yn = "Y" AND parent_id IS NULL -- 최상위 게시글만 조회, 답변글은 별도 로직으로 가져옴
AND (title LIKE CONCAT('%', #{keyword}, '%') OR detail LIKE CONCAT('%', #{keyword}, '%'))
AND insert_date >= #{startDate}
AND insert_date <= CONCAT(#{endDate}, ' 23:59:59')
ORDER BY insert_date DESC LIMIT #{pageSize} OFFSET #{offset}

mapper.update:
update board
set views = views + 1
where board_id = #{boardId}
